import React from "react"
import { Wrap, StyledButton, Icon } from "../styles/button"
import Register from "./Register"
import Dropdown from "../../Header/Dropdown"
import { DropdownBox } from "../../Header/styles/dropdown"
import Signin from "../../Signin/Signin"

class NavButton extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      showModal: false,
      showDrop: false,
      showSign: false,
    }
    this.handleShowModalChange = this.handleShowModalChange.bind(this)
    this.handleShowSignIn = this.handleShowSignIn.bind(this)
    this.handleShowDropdown = this.handleShowDropdown.bind(this)
  }

  async handleShowModalChange() {
    const { showModal } = this.state
    if (!showModal) await this.setState({ showModal: true })
    else await this.setState({ showModal: false })
  }

  async handleShowSignIn() {
    const { showSign } = this.state
    if (!showSign) await this.setState({ showSign: true })
    else await this.setState({ showSign: false })
  }

  async handleShowDropdown() {
    const { showDrop } = this.state
    if (!showDrop) await this.setState({ showDrop: true })
    else await this.setState({ showDrop: false })
  }

  render() {
    const { showModal, showDrop, showSign } = this.state
    // console.log(showDrop)
    console.log(showDrop)
    return (
      <>
        <Wrap>
          <StyledButton onClick={this.handleShowModalChange}>
            {/* <StyledButton onClick={() => console.log("hi")}> */}
            Register
          </StyledButton>
          <StyledButton onClick={this.handleShowSignIn}>Sign In</StyledButton>
          <Icon onClick={this.handleShowDropdown}>
            <i className="fa fa-bars" />
          </Icon>
        </Wrap>
        {/* {showModal === "register" && <Register />} */}
        {showModal ? <Register /> : null}
        {showSign ? <Signin /> : null}
        {showDrop ? <Dropdown /> : <DropdownBox off />}
      </>
    )
  }
}

export default NavButton
