/* eslint-disable no-undef */
/* eslint-disable react/prop-types */
import React, { Component } from "react"
import { connect } from "react-redux"
import {
  ReviewWrapper,
  Avatar,
  Name,
  Date,
  Content,
  Title,
  Comment,
} from "./styles/style"

class Review extends Component {
  constructor(props) {
    super(props)
    this.state = {}
  }

  render() {
    const { reviews } = this.props
    console.log(reviews)
    return (
      <>
        <Title>Review</Title>
        {reviews.length !== 0 ? (
          reviews.map((data) => (
            <ReviewWrapper key={Math.random()}>
              <Avatar src={`/img/users/${data.userImage}`} />
              <Name>{data.user}</Name>
              <Date>
                {data.createdAt.slice(0, 10).split("-").reverse().join(".")}
              </Date>
              <Content>{data.comment}</Content>
            </ReviewWrapper>
          ))
        ) : (
          <Comment>
            Hurry up, to be the first one to write a comment for this tour!✍️
          </Comment>
        )}
      </>
    )
  }
}

const mapStateToProps = (state) => ({
  isLoggedIn: state.header.isLoggedIn,
})

export default connect(mapStateToProps, null)(Review)
