import styled from "styled-components"
import background from "../../../UserPage/Profile/img/background.jpg"
// import { PropCard } from "../../../HomePage/Top/styles/PrompCard"

export const Background = styled.div`
  width: 100%;
  height: 100%;
  background-image: url(${background});
  background-repeat: no-repeat;
  background-size: 100% 100%;
  padding: 100px;
`

export const ProfileWrap = styled.div`
  width: 1150px;
  height: 1000px;
  margin: 0 auto;
  background-color: #eef0f3;
  border-radius: 20px;
  position: relative;
`
export const Left = styled.div`
  width: 300px;
  height: 1000px;
  background-color: #1d2b45;
  display: inline-block;
  border-top-left-radius: 20px;
  border-bottom-left-radius: 20px;
`
export const ListWrap = styled.ul`
  padding-top: 40px;
`
export const List = styled.li`
  padding: 50px 40px;
`
export const Link = styled.a`
  font-family: Poppins;
  font-size: 22px;
  font-weight: 700;
  :hover {
    color: #3ab2b3;
  }
`
export const Icon = styled.img`
  margin-right: 10px;
  vertical-align: bottom;
`

export const Right = styled.div`
  height: 900px;
  width: 800px;
  position: absolute;
  top: 100px;
  left: 720px;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  overflow: hidden;
  overflow-y: scroll;
  overflow-x: hidden;
  scrollbar-color: lightgray #ffffff;
  scrollbar-width: thin;
`
export const Card = styled.div`
  background-color: pink;
  font-size: 26px;
  text-align: center;
  width: 350px;
  height: 530px;
  margin: 20px;
  border: 1px solid #ccc;
  transition-property: transform;
  transition-duration: 0.4s;
  transition-timing-function: ease-in-out;
  box-shadow: 0 1.5rem 4rem rgb(0 0 0 / 10%);
  border-radius: 10px;

  &:hover {
    transform: translateY(-10px);
  }
`
