/* eslint-disable no-script-url */
/* eslint-disable react/destructuring-assignment */
/* eslint-disable react/prop-types */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from "react"
import { connect } from "react-redux"
import * as actionCreators from "../../../components/Header/store/actionCreators"
import accountIcon from "./img/account.png"
import bookingIcon from "./img/booking.png"
import paymentIcon from "./img/payment.png"
import reviewIcon from "./img/review.png"
import eyeIcon from "./img/eye.png"
import eyecloseIcon from "./img/eyeclose.png"
import {
  Background,
  ProfileWrap,
  Left,
  ListWrap,
  List,
  Link,
  Icon,
  Right,
  Header,
  Photo,
  Title,
  Input,
  Button,
  UploadImage,
  IconCurrent,
  IconNew,
  IconConfirm,
} from "./styles/profile"

class Profile extends Component {
  constructor(props) {
    super(props)
    this.state = {
      userFirstName: props.userFirstName,
      userLastName: props.userLastName,
      userEmail: props.userEmail,
      userImage: props.userImage,
      isLoggedIn: props.isLoggedIn,
      currentPassword: "",
      newPassword: "",
      confirmedPassword: "",
      shownCurrent: false,
      shownNew: false,
      shownConfirm: false,
    }
    this.currentVisible = this.currentVisible.bind(this)
    this.newVisible = this.newVisible.bind(this)
    this.confirmVisible = this.confirmVisible.bind(this)
  }

  handleInputChange(e) {
    e.preventDefault()
    this.setState({
      [e.target.id]: e.target.value,
    })
  }

  handleImageChange(e) {
    if (e.target.files && e.target.files[0]) {
      this.setState({
        userImage: e.target.files[0].name,
      })
    }
  }

  currentVisible() {
    if (this.state.shownCurrent === false) {
      this.setState({
        shownCurrent: true,
      })
    } else {
      this.setState({
        shownCurrent: false,
      })
    }
  }

  newVisible() {
    if (this.state.shownNew === false) {
      this.setState({
        shownNew: true,
      })
    } else {
      this.setState({
        shownNew: false,
      })
    }
  }

  confirmVisible() {
    if (this.state.shownConfirm === false) {
      this.setState({
        shownConfirm: true,
      })
    } else {
      this.setState({
        shownConfirm: false,
      })
    }
  }

  render() {
    const {
      userFirstName,
      userLastName,
      userEmail,
      userImage,
      isLoggedIn,
      shownCurrent,
      shownNew,
      shownConfirm,
    } = this.state
    return (
      <>
        <Background>
          <ProfileWrap>
            <Left>
              <ListWrap>
                <List>
                  <Icon src={accountIcon} alt="account" />
                  <Link href="#">Profile</Link>
                </List>
                <List>
                  <Icon src={bookingIcon} alt="booking" />
                  <Link href="#">Bookings</Link>
                </List>
                <List>
                  <Icon src={paymentIcon} alt="payment" />
                  <Link href="#">Payment</Link>
                </List>
                <List>
                  <Icon src={reviewIcon} alt="review" />
                  <Link href="#">Reviews</Link>
                </List>
              </ListWrap>
            </Left>
            <Right>
              <div>
                <Header>Personal Information</Header>
                <Photo
                  src={`/img/users/${userImage}`}
                  id="userImage"
                  alt="profile"
                />
                <UploadImage
                  type="file"
                  name="userImage"
                  accept="image/*"
                  onChange={(e) => this.handleImageChange(e)}
                />
                <Title>First Name</Title>
                <Input
                  type="text"
                  id="userFirstName"
                  value={userFirstName}
                  onChange={(e) => this.handleInputChange(e)}
                />
                <Title>Last Name</Title>
                <Input
                  type="text"
                  id="userLastName"
                  value={userLastName}
                  onChange={(e) => this.handleInputChange(e)}
                />
                {/* <Title>Email</Title>
                <Input
                  type="email"
                  id="userEmail"
                  value={userEmail}
                  onChange={(e) => this.handleInputChange(e)}
                /> */}
                <Button
                  onClick={(e) =>
                    this.props.updateUser(
                      e,
                      userFirstName,
                      userLastName,
                      userEmail,
                      userImage,
                      isLoggedIn
                    )
                  }
                >
                  Update Profile
                </Button>
              </div>
              <div>
                <Title>Current Password</Title>
                <Input
                  type={shownCurrent ? "text" : "password"}
                  id="currentPassword"
                  onChange={(e) => this.handleInputChange(e)}
                />
                <IconCurrent
                  src={shownCurrent ? eyeIcon : eyecloseIcon}
                  onClick={this.currentVisible}
                />
                <Title>New Password</Title>
                <Input
                  type={shownNew ? "text" : "password"}
                  id="newPassword"
                  onChange={(e) => this.handleInputChange(e)}
                />
                <IconNew
                  src={shownNew ? eyeIcon : eyecloseIcon}
                  onClick={this.newVisible}
                />
                <Title>Confirm new password</Title>
                <Input
                  type={shownConfirm ? "text" : "password"}
                  id="confirmedPassword"
                  onChange={(e) => this.handleInputChange(e)}
                />
                <IconConfirm
                  src={shownConfirm ? eyeIcon : eyecloseIcon}
                  onClick={this.confirmVisible}
                />
                <Button
                  onClick={(e) =>
                    this.props.updatePassword(
                      e,
                      this.state.currentPassword,
                      this.state.newPassword,
                      this.state.confirmedPassword,
                      this.state.userEmail
                    )
                  }
                >
                  Update Password
                </Button>
              </div>
            </Right>
          </ProfileWrap>
        </Background>
      </>
    )
  }
}

const mapStateToProps = (state) => ({
  isLoggedIn: state.header.isLoggedIn,
  userFirstName: state.header.userFirstName,
  userLastName: state.header.userLastName,
  userEmail: state.header.userEmail,
  userImage: state.header.userImage,
})

const mapDispatchToProps = (dispatch) => ({
  updateUser(e, userFirstName, userLastName, userEmail, userImage, isLoggedIn) {
    dispatch(
      actionCreators.updateUser(
        e,
        userFirstName,
        userLastName,
        userEmail,
        userImage,
        isLoggedIn
      )
    )
  },
  updatePassword(
    e,
    currentPassword,
    newPassword,
    confirmedPassword,
    userEmail
  ) {
    dispatch(
      actionCreators.updatePassword(
        e,
        currentPassword,
        newPassword,
        confirmedPassword,
        userEmail
      )
    )
  },
})

export default connect(mapStateToProps, mapDispatchToProps)(Profile)
