/* eslint-disable no-unused-vars */
/* eslint-disable camelcase */
/* eslint-disable react/destructuring-assignment */
/* eslint-disable prettier/prettier */
/* eslint-disable react/prop-types */
import React, { Component } from "react"
import { connect } from "react-redux"
import { Link, withRouter } from "react-router-dom"
import * as actionCreators from "./store/actionCreators"
import { Wrap, Left, Right, Item, Avatar, UserName, Logout } from "./styles/navbar"

class Navbar_logout extends Component {
  render() {
    // eslint-disable-next-line react/prop-types
    const { location } = this.props
    console.log(this.props.isShow)
    return (
      <Wrap>
        <Left activeKey={location.pathname}>
          <Link to="/">
            <Item href="/">BestTraveller</Item>
          </Link>
          <Link to="/">
            <Item className={location.pathname === "/home" && "active"}>
              Home
            </Item>
          </Link>
          <Link to="/packages">
            <Item className={location.pathname === "/packages" && "active"}>
              Packages
            </Item>
          </Link>
          <Link to="/about-us">
            <Item className={location.pathname === "/about" && "active"}>
              About us
            </Item>
          </Link>
          <Link to="/contact-us">
            <Item className={location.pathname === "/contact" && "active"}>
              Contact us
            </Item>
          </Link>
        </Left>
        <Right>
          {this.props.isShow ? <Logout onClick={this.props.logout}>Log out</Logout> : null }
          <Link to = "/user">
            <Avatar src={`/img/users/${this.props.userImage}`} alt="User" />
          </Link>
          <UserName>{this.props.userFirstName}</UserName>
        </Right>
      </Wrap>
    )
  }
}

const mapStateToProps = (state) => ({
  userFirstName: state.header.userFirstName,
  userImage: state.header.userImage,
})

const mapDispatchToProps = (dispatch) => ({
    logout() {
      dispatch(actionCreators.logout())
    },
  })

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Navbar_logout))
