/* eslint-disable no-undef */
/* eslint-disable no-alert */
/* eslint-disable no-unused-vars */
/* eslint-disable camelcase */
import axios from "axios"
import * as actionTypes from "./actionTypes"

const get_tour_info = (tour, isLoading) => ({
  type: actionTypes.GET_TOUR_INFO,
  tour,
  isLoading,
})

export const getTour = (slug) => (dispatch) => {
  axios.get(`http://127.0.0.1:8000/api/v1/tours/${slug}`).then((response) => {
    const tour = response.data
    const isLoading = false
    const action = get_tour_info(tour, isLoading)
    dispatch(action)
  })
}

export const sendComment =
  (userFirstName, userImage, content, tour, slug) => (dispatch) => {
    console.log(tour)
    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/api/v1/reviews/",
      data: {
        comment: content,
        user: userFirstName,
        userImage: userImage,
        tour: tour._id,
      },
      withCredentials: true,
    }).then((res) => {
      console.log(res.data)
      const reviewId = res.data._id
      axios({
        method: "POST",
        url: `http://127.0.0.1:8000/api/v1/tours/${tour._id}/reviews/${reviewId}`,
        withCredentials: true,
      }).then(() => {
        alert("Successfully comment")
        window.location = `/tours/${slug}`
      })
    })
  }
