  @media screen and (max-width: 320px) {
    margin-left: 20px;
  }
`

export const Avatar = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background-image: url(${avatar});
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  display: inline-block;
`

export const Name = styled.span`
  position: absolute;
  top: 0;
  left: 90px;
  font-family: Poppins;
  font-size: 30px;
  font-weight: bold;
  margin-bottom: 10px;
  @media screen and (max-width: 1440px) {
    left: 70px;
    font-size: 20px;
  }
`

export const Date = styled.span`
  position: absolute;
  top: 45px;
  left: 90px;
  font-family: Poppins;
  font-size: 30px;
  color: #5e6d77;
  @media screen and (max-width: 1440px) {
    top: 30px;
    left: 70px;
    font-size: 20px;
