/* eslint-disable react/prop-types */
import React, { Component } from "react"
import MaterialIcon from "material-icons-react"
import {
  Title,
  Location,
  Image,
  Specification,
  Item,
  ItemTitle,
  ItemSubTitle,
} from "./styles/banner"
import { ReactComponent as LocationSVG } from "./img/location.svg"
import "./styles/banner.css"

class Banner extends Component {
  constructor(props) {
    super(props)
    this.state = {}
  }

  capitalize(string) {
    return string.charAt(0).toUpperCase() + string.slice(1)
  }

  render() {
    const { title, state, image, price, startDate, endDate } = this.props
    let { city } = this.props
    const startDateConvert = startDate.split("/").join(".")
    const endDateConvert = endDate.split("/").join(".")
    city = this.capitalize(city)
    return (
      <>
        <Title>{title}</Title>
        <Location>
          <LocationSVG style={{ marginRight: "10px" }} />
          {city}, {state}
        </Location>
        <Image src={`/img/tours/${image}.jpg`} alt="tourImage" />
        <Specification>
          <Item>
            <MaterialIcon icon="local_offer" size={55} />
            <ItemTitle>Price</ItemTitle>
            <ItemSubTitle>${price}</ItemSubTitle>
          </Item>
          <Item>
            <MaterialIcon icon="today" size={55} color="#3ab2b3" />
            <ItemTitle>Start Date</ItemTitle>
            <ItemSubTitle>{startDateConvert}</ItemSubTitle>
          </Item>
          <Item>
            <MaterialIcon icon="today" size={55} color="#3ab2b3" />
            <ItemTitle>End Date</ItemTitle>
            <ItemSubTitle>{endDateConvert}</ItemSubTitle>
          </Item>
        </Specification>
      </>
    )
  }
}

export default Banner
