/* eslint-disable react/prop-types */
import React, { Component } from "react"
import axios from "axios"
import swal from "sweetalert"
import configuration from "../../config/config"
import { SearchBarWrapper, SearchBar, SendButton } from "./styles/footer"

class Search extends Component {
  constructor(props) {
    super(props)
    this.state = {
      email: "",
    }
    this.handleInputChange = this.handleInputChange.bind(this)
  }

  handleInputChange(e) {
    this.setState({
      email: e.target.value,
    })
  }

  validateEmail(email) {
    const re =
      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    return re.test(String(email).toLowerCase())
  }

  subscribe(e, email) {
    e.preventDefault()
    if (!this.validateEmail(email)) {
      swal("Oops", "Please enter valid email address", "error")
      return
    }
    axios({
      method: "POST",
      url: `${configuration.api.backend_api}/api/v1/auth/subscribe`,
      data: {
        email,
      },
      withCredentials: true,
    })
      .then((res) => {
        swal("Thanks!", "You have subscribed our newsletter!", "success")
      })
      .catch((err) => {
        swal("Oops!", "Please type correct email address!", "error")
      })
  }

  render() {
    const { email } = this.state
    return (
      <>
        <SearchBarWrapper action="#" method="post">
          <SearchBar
            type="text"
            placeholder="Email"
            onChange={this.handleInputChange}
          />
          <SendButton onClick={(e) => this.subscribe(e, email)}>
            SEND
          </SendButton>
        </SearchBarWrapper>
      </>
    )
  }
}

export default Search
