/* eslint-disable no-unused-vars */
/* eslint-disable camelcase */
/* eslint-disable react/prop-types */
/* eslint-disable react/no-access-state-in-setstate */
/* eslint-disable react/destructuring-assignment */
import axios from "axios"
import { React, Component } from "react"
import { Tab, Tabs, TabList } from "react-tabs"
import configuration from "../../../../config/config"
import PromCard from "./PromCard"
import { Loading } from "../../../../components/Loading/Loading"
import { Panel } from "../styles/StateTab"

class StateTab extends Component {
  constructor(props) {
    super(props)
    this.state = {
      tours: [],
      isLoading: true,
    }
    this.stateTour = this.stateTour.bind(this)
  }

  componentDidMount() {
    // this.props.getAllTours()
    axios
      .get(`${configuration.api.backend_api}/api/v1/tours`)
      .then((result) => {
        this.setState({
          tours: result.data,
          isLoading: false,
        })
      })
  }

  capitalize(string) {
    return string.charAt(0).toUpperCase() + string.slice(1)
  }

  // getToursByState
  stateTour(data, i, stateName) {
    data.city = this.capitalize(data.city)
    if (data.state === stateName) {
      return (
        <PromCard
          id={i}
          key={data._id}
          source={data.image} // image, check out how to get this image in database.
          title={data.title}
          price={data.price}
          city={data.city}
          days={data.itinerary.length}
          introduction={data.introduction}
          slug={data.slug}
        />
      )
    }
  }

  render() {
    const { isLoading } = this.state
    if (isLoading) return <Loading>Loading...👩🏻‍💻</Loading>
    return (
      <>
        <Tabs>
          <TabList>
            <Tab>QLD</Tab>
            <Tab>NSW</Tab>
            <Tab>VIC</Tab>
            <Tab>SA</Tab>
            <Tab>TAS</Tab>
            <Tab>WA</Tab>
            <Tab>ACT</Tab>
            <Tab>NT</Tab>
          </TabList>
          {/* QLD */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data, i) => this.stateTour(data, i, "QLD"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* NSW */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data, i) => this.stateTour(data, i, "NSW"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* VIC */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data, i) => this.stateTour(data, i, "VIC"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* SA */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data, i) => this.stateTour(data, i, "SA"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* TAS */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data, i) => this.stateTour(data, i, "TAS"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* WA */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data, i) => this.stateTour(data, i, "WA"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* ACT */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data, i) => this.stateTour(data, i, "ACT"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* NT */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data, i) => this.stateTour(data, i, "NT"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
        </Tabs>
      </>
    )
  }
}

export default StateTab
