/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from "react"
import InputWrapper from "./InputWrapper"
import SignWrapper from "./SignWrapper"
import { Globalstyle } from "../../style"
import { SigninBox, CloseIcon, Header } from "./styles/signin"

import closeIcon from "./img/cancel.png"

class Signin extends Component {
  constructor(props) {
    super(props)
    this.state = {
      showSignIn: true,
    }
    this.handleShowSignIn = this.handleShowSignIn.bind(this)
  }

  handleShowSignIn(newShowSignIn) {
    this.setState({
      showSignIn: newShowSignIn,
    })
  }

  render() {
    const { showSignIn } = this.state
    return (
      <>
        {showSignIn === true && (
          <>
            <SigninBox>
              <a href="#" onClick={() => this.handleShowSignIn(undefined)}>
                <CloseIcon src={closeIcon} alt="close" />
              </a>
              <Header>Sign in</Header>
              <InputWrapper />
              <SignWrapper />
            </SigninBox>
            <Globalstyle />
          </>
        )}
      </>
    )
  }
}

export default Signin
