/* eslint-disable no-return-assign */
/* eslint-disable react/prop-types */
import React from "react"
import { Formik } from "formik"
import * as Yup from "yup"
import axios from "axios"

import {
  Title,
  Input,
  Errors,
  RememberLabel,
  ForgotPassword,
  SigninButton,
} from "./styles/signin"

const login = async (inputData) => {
  try {
    const res = await axios({
      method: "POST",
      url: "http://localhost:8000/api/v1/auth",
      data: {
        email: inputData.email,
        password: inputData.password,
      },
    })
    console.log(res.data)
  } catch (error) {
    console.log(error.message)
  }
}

const InputWrapper = () => (
  <Formik
    initialValues={{ email: "", password: "" }}
    //********Using Yum for validation********/
    validationSchema={Yup.object().shape({
      email: Yup.string()
        .email("Invalid email address")
        .required("Email cannot be empty"),
      password: Yup.string()
        .required("Password cannot be empty")
        .min(8, "Password is too short - should be 8 chars minimum.")
        .matches(/(?=.*[0-9])/, "Password must contain a number."),
    })}
    onSubmit={(values, { setSubmitting }) => {
      setTimeout(() => {
        login(values)
        setSubmitting(false)
      }, 500)
    }}
  >
    {(props) => {
      const {
        values,
        touched,
        errors,
        isSubmitting,
        handleChange,
        handleBlur,
        handleSubmit,
      } = props

      return (
        <form onSubmit={handleSubmit}>
          <Title>Email</Title>
          <Input
            name="email"
            type="email"
            placeholder="Enter your email address..."
            onFocus={(e) => (e.target.placeholder = "")}
            value={values.email}
            onChange={handleChange}
            onBlur={handleBlur}
            className={errors.email && touched.email}
          />
          {errors.email && touched.email && <Errors>{errors.email}</Errors>}
          <Title style={{ marginTop: "28px" }}>Password</Title>
          <Input
            name="password"
            type="password"
            placeholder="********"
            onFocus={(e) => (e.target.placeholder = "")}
            onChange={handleChange}
            onBlur={handleBlur}
            className={errors.password && touched.password}
          />
          {errors.password && touched.password && (
            <Errors>{errors.password}</Errors>
          )}
          <div style={{ marginTop: "30px" }}>
            <RememberLabel>
              <input type="radio" /> Remember me
            </RememberLabel>
            <ForgotPassword href="#"> Forgot password? </ForgotPassword>
          </div>

          <SigninButton type="submit" disabled={isSubmitting}>
            Sign in
          </SigninButton>
        </form>
      )
    }}
  </Formik>
)

export default InputWrapper
