/* eslint-disable import/no-unresolved */
/* eslint-disable react/no-unused-state */
/* eslint-disable no-undef */
/* eslint-disable no-alert */
/* eslint-disable node/no-unsupported-features/es-syntax */
import React from "react"
import axios from "axios"
import closeIcon from "./img/cancel.png"
import {
  Card,
  Title,
  InputContainer,
  Label,
  Input,
  Button,
  Close,
  CloseIcon,
} from "./styles/register"
import RegisterSuccessful from "../Bob/components/RegisterSuccessful"

class Register extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      showModal: true,
      isRegister: false,
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      confirmPassword: "",
    }
    this.handleShowModalChange = this.handleShowModalChange.bind(this)
    this.setData = this.setData.bind(this)
  }

  handleShowModalChange(newShowModal) {
    this.setState({
      showModal: newShowModal,
    })
  }

  setData(e) {
    this.setState({
      [e.target.id]: e.target.value,
    })
  }

  signUp(firstName, lastName, email, password) {
    axios({
      method: "POST",
      url: "http://localhost:8000/api/v1/users",
      data: {
        firstName: firstName,
        lastName: lastName,
        email: email,
        password: password,
        role: "user",
        dateOfBirth: "29/09/1999",
      },
    })
      .then((res) => {
        this.setState({
          isRegister: true,
        })
        console.log(res)
      })
      .catch((err) => {
        console.log(err)
      })
  }

  render() {
    const { showModal, firstName, lastName, email, password, isRegister } =
      this.state
    let component
    if (showModal === true && isRegister === false) {
      component = (
        <Card>
          <Close onClick={() => this.handleShowModalChange(undefined)}>
            <CloseIcon src={closeIcon} alt="close" />
          </Close>
          <InputContainer>
            <Title>Register</Title>
            <Label>First Name</Label>
            <Input
              id="firstName"
              onChange={(e) => this.setData(e)}
              type="text"
              placeholder="Jack"
            />
            <Label>Last Name</Label>
            <Input
              id="lastName"
              onChange={this.setData}
              type="text"
              placeholder="Anderson"
            />
            <Label>Email</Label>
            <Input
              id="email"
              onChange={(e) => this.setData(e)}
              type="email"
              placeholder="Jack.anderson@gmail.com"
            />
            <Label>Password</Label>
            <Input
              onChange={(e) => this.setData(e)}
              type="password"
              placeholder="********"
              id="password"
            />
            <Label>Confirm Password</Label>
            <Input
              onChange={(e) => this.setData(e)}
              type="password"
              placeholder="********"
              id="confirmPassword"
            />
            <Button
              onClick={() => this.signUp(firstName, lastName, email, password)}
            >
              Register
            </Button>
          </InputContainer>
        </Card>
      )
    } else if (showModal === undefined) {
      component = null
    } else {
      component = <RegisterSuccessful />
    }

    return <>{component}</>
  }
}

export default Register
