import React, { Component } from "react"
import { StyledButton, DropdownBox } from "./styles/dropdown"
import Register from "../Yang/components/Register"
import Signin from "../Signin/Signin"

class Dropdown extends Component {
  constructor(props) {
    super(props)
    this.state = {
      showModal: false,
      showSign: false,
    }
    this.handleShowModalChange = this.handleShowModalChange.bind(this)
    this.handleShowSignChange = this.handleShowSignChange.bind(this)
  }

  async handleShowModalChange() {
    const { showModal } = this.state
    if (!showModal) await this.setState({ showModal: true })
    else await this.setState({ showModal: false })
  }

  async handleShowSignChange() {
    const { showSign } = this.state
    if (!showSign) await this.setState({ showSign: true })
    else await this.setState({ showSign: false })
  }

  render() {
    const { showModal, showSign } = this.state
    return (
      <>
        <DropdownBox>
          <StyledButton
            style={{
              display: "block",
              marginLeft: "20px",
              width: "87%",
              height: "40px",
              textAlign: "left",
              borderBottom: "1px solid #ccc",
            }}
          >
            <a href="/">Home</a>
          </StyledButton>
          <StyledButton
            style={{
              display: "block",
              marginLeft: "20px",
              width: "87%",
              height: "40px",
              textAlign: "left",
              borderBottom: "1px solid #ccc",
              visibility: "visible",
            }}
          >
            <a href="/packages">Packages</a>
          </StyledButton>
          <StyledButton
            style={{
              display: "block",
              marginLeft: "20px",
              width: "87%",
              height: "40px",
              textAlign: "left",
              borderBottom: "1px solid #ccc",
            }}
          >
            <a href="/about-us">About Us</a>
          </StyledButton>
          <StyledButton
            style={{
              display: "block",
              marginLeft: "20px",
              width: "87%",
              height: "40px",
              textAlign: "left",
              borderBottom: "1px solid #ccc",
            }}
          >
            <a href="/contact-us">Contact Us</a>
          </StyledButton>
          <StyledButton
            onClick={this.handleShowModalChange}
            style={{
              display: "block",
              marginLeft: "20px",
              width: "87%",
              height: "40px",
              textAlign: "left",
              borderBottom: "1px solid #ccc",
            }}
          >
            Register
          </StyledButton>
          <StyledButton
            onClick={this.handleShowSignChange}
            style={{
              display: "block",
              marginLeft: "20px",
              width: "87%",
              height: "40px",
              textAlign: "left",
            }}
          >
            Sign In
          </StyledButton>
        </DropdownBox>
        {showModal ? <Register /> : null}
        {showSign ? <Signin /> : null}
      </>
    )
  }
}

export default Dropdown
