/* eslint-disable react/no-access-state-in-setstate */
/* eslint-disable react/destructuring-assignment */

import axios from "axios"
import { React, Component } from "react"
import { Tab, Tabs, TabList } from "react-tabs"
import PromCard from "./PromCard"
// import Sydney from "../img/sydney.jpg"
// import Family from "../img/family.png"
// import WildLife from "../img/wildlife.png"
// import Melbourne from "../img/melbourne.jpg"
// import Newcastle from "../img/newcastle.jpg"
// import GreatBarrier from "../img/great-barrier.jpg"
import { Panel } from "../styles/StateTab"

class StateTab extends Component {
  constructor(props) {
    super(props)
    this.state = {
      tours: [],
    }
    this.stateTour = this.stateTour.bind(this)
  }

  componentDidMount() {
    axios.get(`http://localhost:3001/api/v1/tours`).then((result) => {
      this.setState({
        tours: result.data,
      })
    })
  }

  // getToursByState
  stateTour(data, stateName) {
    if (data.state === stateName) {
      return (
        <PromCard
          key={data._id}
          source={data.image} // image, check out how to get this image in database.
          title={data.title}
          price={data.price}
          city={data.city}
          days={data.itinerary.length}
          introduction={data.introduction}
        />
      )
    }
  }

  render() {
    return (
      <>
        <Tabs>
          <TabList>
            <Tab>QLD</Tab>
            <Tab>NSW</Tab>
            <Tab>VIC</Tab>
            <Tab>SA</Tab>
            <Tab>TAS</Tab>
            <Tab>WA</Tab>
            <Tab>ACT</Tab>
            <Tab>NT</Tab>
          </TabList>
          {/* QLD */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data) => this.stateTour(data, "QLD"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* NSW */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data) => this.stateTour(data, "NSW"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* VIC */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data) => this.stateTour(data, "VIC"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* SA */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data) => this.stateTour(data, "SA"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* TAS */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data) => this.stateTour(data, "TAS"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* WA */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data) => this.stateTour(data, "WA"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* ACT */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data) => this.stateTour(data, "ACT"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
          {/* NT */}
          <Panel>
            {this.state.tours.length ? (
              this.state.tours.map((data) => this.stateTour(data, "NT"))
            ) : (
              <h2>New tours are coming...</h2>
            )}
          </Panel>
        </Tabs>
      </>
    )
  }
}

export default StateTab
