/* eslint-disable camelcase */
/* eslint-disable no-unused-vars */
/* eslint-disable no-alert */
/* eslint-disable no-undef */
import axios from "axios"
import * as actionTypes from "./actionTypes"

const userLogin = (
  userEmail,
  userFirstName,
  userLastName,
  userPassword,
  userImage
) => ({
  type: actionTypes.USER_LOGIN,
  userEmail,
  userFirstName,
  userLastName,
  userPassword,
  userImage,
})

const userLogout = () => ({
  type: actionTypes.USER_LOGOUT,
})

const update_user_info = (
  userFirstName,
  userLastName,
  userEmail,
  userImage,
  isLoggedIn
) => ({
  type: actionTypes.UPDATE_USER_INFO,
  userFirstName,
  userLastName,
  userEmail,
  userImage,
  isLoggedIn,
})

const update_user_pass = (
  currentPassword,
  newPassword,
  confirmedPassword,
  userEmail
) => ({
  type: actionTypes.UPDATE_USER_PASS,
  currentPassword,
  newPassword,
  confirmedPassword,
  userEmail,
})

export const login = (values) => (dispatch) => {
  axios({
    method: "POST",
    url: "http://127.0.0.1:8000/api/v1/auth/login",
    data: {
      email: values.email,
      password: values.password,
    },
    withCredentials: true,
  })
    .then((res) => {
      console.log(res.data.user)
      const userEmail = res.data.user.email
      const userPassword = res.data.user.password
      const userFirstName = res.data.user.firstName
      const userLastName = res.data.user.lastName
      const userImage = res.data.user.image
      const action = userLogin(
        userEmail,
        userFirstName,
        userLastName,
        userPassword,
        userImage
      )
      dispatch(action)
      alert("Successfully logged in")
      window.location = "/"
    })
    .catch((err) => {
      console.log(err)
    })
}

export const logout = () => (dispatch) => {
  const action = userLogout()
  dispatch(action)
  alert("Successfully logged out")
}

export const updateUser = (
  e,
  userFirstName,
  userLastName,
  userEmail,
  userImage,
  isLoggedIn
) => {
  e.preventDefault()
  return (dispatch) => {
    axios({
      method: "PATCH",
      url: "http://127.0.0.1:8000/api/v1/users/updateMe",
      data: {
        firstName: userFirstName,
        lastName: userLastName,
        email: userEmail,
        image: userImage,
      },
      withCredentials: true,
    })
      .then((res) => {
        console.log(res)
        const action = update_user_info(
          userFirstName,
          userLastName,
          userEmail,
          userImage,
          isLoggedIn
        )
        dispatch(action)
        alert("Successfully updated")
      })
      .catch((err) => {
        console.log(err)
      })
  }
}

export const updatePassword = (
  e,
  currentPassword,
  newPassword,
  confirmedPassword,
  userEmail
) => {
  e.preventDefault()
  return (dispatch) => {
    axios({
      method: "PATCH",
      url: "http://127.0.0.1:3000/api/v1/auth/updatePassword",
      data: {
        currentPassword: currentPassword,
        newPassword: newPassword,
        confirmedPassword: confirmedPassword,
        email: userEmail,
      },
      withCredentials: true,
    })
      .then((res) => {
        alert("Password successfully updated")
        const action = update_user_pass(
          currentPassword,
          newPassword,
          confirmedPassword,
          userEmail
        )
        dispatch(action)
      })
      .catch((err) => {
        console.log(err)
      })
  }
}
