/* eslint-disable camelcase */
/* eslint-disable react/jsx-pascal-case */
/* eslint-disable react/destructuring-assignment */
/* eslint-disable react/prop-types */
import React, { Component } from "react"
import { connect } from "react-redux"
import * as actionCreators from "./store/actionCreators"
import Navbar_logout from "../../components/Header/Navbar_logout"
import Navbar from "../../components/Header/Navbar"
import Banner from "./Banner/Banner"
import Overview from "./Overview/Overview"
import Highlight from "./HighLight/Highlight"
import Itinerary from "./Itinerary/Itinerary"
import Mapbox from "./Mapbox/Mapbox"
import Review from "./Review/Review"
import BookNow from "./BookNow/BookNow"
import Comment from "./Comment/CommentApp"
import Footer from "../../components/Footer/Footer"
import "./styles/style.css"

class Detail extends Component {
  constructor(props) {
    super(props)
    this.state = {}
  }

  componentDidMount() {
    const { slug } = this.props.match.params
    this.props.getTour(slug)
  }

  render() {
    const { tour, isLoading } = this.props
    console.log(tour)
    const { slug } = this.props.match.params
    if (isLoading)
      return (
        <div className="loadEffect">
          <span />
          <span />
          <span />
          <span />
          <span />
          <span />
          <span />
          <span />
        </div>
      )
    return (
      <>
        {this.props.isLoggedIn ? <Navbar_logout /> : <Navbar />}
        <Banner
          title={tour.title}
          city={tour.city}
          state={tour.state}
          image={tour.image}
          price={tour.price}
          startDate={tour.startDate}
          endDate={tour.endDate}
        />
        <Overview overview={tour.introduction} />
        <Highlight highlight={tour.subtitle} />
        <Itinerary itinerary={tour.itinerary} />
        <Mapbox />
        <Review reviews={tour.reviews} />
        {this.props.isLoggedIn ? <Comment slug={slug} /> : null}
        <BookNow />
        <Footer />
      </>
    )
  }
}

const mapStateToProps = (state) => ({
  isLoggedIn: state.header.isLoggedIn,
  tour: state.tour.tour,
})

const mapDispatchToProps = (dispatch) => ({
  getTour(slug) {
    dispatch(actionCreators.getTour(slug))
  },
})

export default connect(mapStateToProps, mapDispatchToProps)(Detail)
