/* eslint-disable react/destructuring-assignment */
/* eslint-disable react/prop-types */
import React, { Component } from "react"
import axios from "axios"
import { connect } from "react-redux"
import { loadStripe } from "@stripe/stripe-js"
import { Wrapper, Title, Content, Button } from "./styles/style"

const stripePromise = loadStripe(
  "pk_test_51JH5JMKu7kyZrQEG6Lo8u0p4bMPRIYLR2nwyj3JmeUFMKUjZU0Hax085w4Z4VfX0uFfKI4UQthFV2NPDr842JTrL00XBO0dRBQ"
)
class BookNow extends Component {
  constructor(props) {
    super(props)
    this.state = {}
  }

  async bookTour(userId, tourId) {
    // get checkout session from API
    const session = await axios(
      `http://127.0.0.1:3000/api/v1/bookings/checkout-session/${userId}/${tourId}`
    )
    console.log(session)
    // create checkout from charge credit card
    const stripe = await stripePromise
    setTimeout(async () => {
      await stripe.redirectToCheckout({
        sessionId: session.data.session.id,
      })
    }, 1000)
  }

  render() {
    const userId = "liuxiaoyangyang0327@gmail.com"
    const tourId = "60fe527b378ddb8876802f99"
    return (
      <>
        <Wrapper>
          <Title>What are you waiting for?</Title>
          <Content>What a wonderful trip! Make it yours today!</Content>
          {this.props.isLoggedIn ? (
            <Button onClick={() => this.bookTour(userId, tourId)}>
              Book Now
            </Button>
          ) : (
            <Button>Log in to book tour</Button>
          )}
        </Wrapper>
      </>
    )
  }
}

const mapStateToProps = (state) => ({
  isLoggedIn: state.header.isLoggedIn,
})

export default connect(mapStateToProps, null)(BookNow)
