import styled from "styled-components"
import device from "../../../../responsive/Device"
import { Column, Row } from "./utils"

export const Img = styled.img`
  height: 245px;
`

export const Button = styled.button`
  width: 90px;
  padding: 5px 0;
  border-radius: 45px;
  margin-top: 5px;
  border: none;
  font-weight: 600;
  font-size: 12px;
  color: white;
  font-family: Poppins;
  background-color: #3ab2b3;
`

export const PropCard = styled(Column)`
  width: 380px;
  margin: 20px;
  border: 1px solid #ccc;
  transition-property: transform;
  transition-duration: 0.4s;
  transition-timing-function: ease-in-out;
  box-shadow: 0 1.5rem 4rem rgb(0 0 0 / 10%);
  &:hover {
    transform: translateY(-10px);
  }
  @media ${device.mobileS} {
    width: 90%;
    border: none;
  }
  @media ${device.mobileM} {
    width: 380px;
  }
`

export const PrompInfo = styled(Column)`
  justify-content: flex-start;
  padding: 0 20px 10px;
`
export const Title = styled(Row)`
  align-items: center;
  justify-content: space-between;
`
export const Location = styled(Row)`
  align-items: center;
  justify-content: flex-start;
  color: gray;
  margin: 10px 0;
`

export const LocationText = styled.p`
  font-family: Poppins;
  margin-left: 5px;
`

export const PropText = styled.p`
  line-height: 20px;
  text-align: start;
  margin: 10px 0;
`

export const Line = styled.hr`
  background-color: #ccc;
`

export const CardTitle = styled.h3`
  font-family: Poppins;
  font-size: 18px;
  font-weight: 600;
  margin: 20px 0;
`
