import styled from "styled-components"

export const HighLightWrapper = styled.div`
  margin-top: 30px;
  border-bottom: 1px solid #d7dce3;
  @media screen and (max-width: 2560px) {
    width: 30%;
    margin-left: 130px;
    position: absolute;
    top: 700px;
    left: 50%;
  }
  @media screen and (max-width: 1440px) {
    margin-left: 70px;
  }
  @media screen and (max-width: 1024px) {
    position: unset;
    width: 90%;
  }
  @media screen and (max-width: 768px) {
    margin-left: 40px;
  }
  @media screen and (max-width: 525px) {
    margin-left: 35px;
  }
  @media screen and (max-width: 375px) {
    margin-left: 25px;
  }
  @media screen and (max-width: 320px) {
    margin-left: 20px;
  }
`

export const HighLightTitle = styled.p`
  margin-bottom: 25px;
  font-size: 36px;
  font-weight: bold;
  font-family: Poppins;
  @media screen and (max-width: 1440px) {
    font-size: 28px;
  }
`

export const HighLightParagraph = styled.p`
  font-size: 26px;
  color: #5e6d77;
  font-family: Poppins;
  line-height: normal;
  margin-bottom: 30px;
  @media screen and (max-width: 1440px) {
    font-size: 18px;
  }
`
