/* eslint-disable no-alert */
/* eslint-disable no-undef */
/* eslint-disable react/destructuring-assignment */
/* eslint-disable react/prop-types */
import React, { Component } from "react"
import axios from "axios"
import swal from "sweetalert"
import { SearchBarWrapper, SearchBar, SendButton } from "./styles/footer"

class Search extends Component {
  constructor(props) {
    super(props)
    this.state = {
      email: "",
    }
    this.handleInputChange = this.handleInputChange.bind(this)
  }

  handleInputChange(e) {
    this.setState({
      email: e.target.value,
    })
  }

  subscribe(e, email) {
    e.preventDefault()
    axios({
      method: "POST",
      url: "http://127.0.0.1:8000/api/v1/auth/subscribe",
      data: {
        email,
      },
      withCredentials: true,
    })
      .then((res) => {
        swal("Successfully subscribed")
        window.location = "/"
      })
      .catch((err) => {
        console.log(err)
      })
  }

  render() {
    return (
      <>
        <SearchBarWrapper action="#" method="post">
          <SearchBar
            type="text"
            placeholder="Email"
            onChange={this.handleInputChange}
          />
          <SendButton onClick={(e) => this.subscribe(e, this.state.email)}>
            SEND
          </SendButton>
        </SearchBarWrapper>
      </>
    )
  }
}

export default Search
