const validate = (name, data) => {
  const value = data[name]
  // console.log("Name", name, "Value", value)
  switch (name) {
    case "firstName": {
      if (!value.value) {
        return "Please input your firstname"
      }
      if (value.value.toString().length < 2) {
        return "Firstname should be at least 2 char"
      }
      return ""
    }
    case "lastName": {
      if (!value.value) {
        return "Please input your lastname"
      }
      // if (data.lastName.length < 2) {
      if (value.value.toString().length < 2) {
        return "Lastname should be at least 2 char"
      }
      return ""
    }
    case "email": {
      if (!value.value) {
        return "Please input your email"
      }
      const EMAIL_REGEXP =
        /^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/
      if (!EMAIL_REGEXP.test(value.value)) {
        return "Please input a valid email"
      }
      return ""
    }
    case "password": {
      if (!value.value) {
        return "Please input your password"
      }
      // if (data.password.length < 6) {
      if (value.value.toString().length < 6) {
        return "Password should be at least 6 char"
      }
      return ""
    }
    case "confirmPassword": {
      if (!value.value) {
        return "Please input your confirm password"
      }
      if (value.value !== data.password.value) {
        return "Confirm password does not match to password"
      }
      return ""
    }
    default:
      return ""
  }
}
export default validate
