import styled from "styled-components"

export const Wrapper = styled.div`
  width: 800px;
  height: 600px;
  background-color: #f6fafd;
  margin: 100px auto;
`
export const Text = styled.p`
  width: 400px;
  padding: 80px 0 50px;
  margin: 0 auto;
  text-align: center;
  font-size: 16px;
  font-weight: 400;
  font-family: Poppins;
`
export const Title = styled.p`
  font-size: 18px;
  font-family: Poppins;
`
export const InputBox = styled.input`
  width: 300px;
  height: 50px;
  display: block;
`
export const Button = styled.button`
  width: 300px;
  height: 50px;
`
