import React, { Component } from "react"
import { connect } from "react-redux"
import { Link, withRouter } from "react-router-dom"
import PropTypes from "prop-types"
import * as actionCreators from "./store/actionCreators"
import {
  Wrap,
  Left,
  Right,
  Item,
  Avatar,
  UserName,
  Logout,
} from "./styles/navbar"

class NavbarLogout extends Component {
  render() {
    const { isShow, logout, userImage, userFirstName } = this.props
    return (
      <Wrap>
        <Left>
          <Link to="/">
            <Item href="/">BestTraveller</Item>
          </Link>
          <Link to="/">
            <Item>Home</Item>
          </Link>
          <Link to="/packages">
            <Item>Packages</Item>
          </Link>
          <Link to="/about-us">
            <Item>About us</Item>
          </Link>
          <Link to="/contact-us">
            <Item>Contact us</Item>
          </Link>
        </Left>
        <Right>
          {isShow ? <Logout onClick={logout}>Log out</Logout> : null}
          <Link to="/user">
            <Avatar src={`/img/users/${userImage}`} alt="User" />
          </Link>
          <UserName>{userFirstName}</UserName>
        </Right>
      </Wrap>
    )
  }
}

NavbarLogout.propTypes = {
  isShow: PropTypes.bool,
  logout: PropTypes.func.isRequired,
  userImage: PropTypes.string.isRequired,
  userFirstName: PropTypes.string.isRequired,
}

NavbarLogout.defaultProps = {
  isShow: undefined,
}

const mapStateToProps = (state) => ({
  userFirstName: state.header.userFirstName,
  userImage: state.header.userImage,
})

const mapDispatchToProps = (dispatch) => ({
  logout() {
    dispatch(actionCreators.logout())
  },
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(NavbarLogout))
