/* eslint-disable react/prop-types */
/* eslint-disable react/destructuring-assignment */
/* eslint-disable react/button-has-type */
import React, { Component } from "react"
import { connect } from "react-redux"
import * as actionCreators from "../store/actionCreators"
import {
  CommentWrap,
  CommentField,
  FieldName,
  FieldInput,
  InputArea,
  Button,
} from "./styles/styles"

class CommentInput extends Component {
  constructor(props) {
    super(props)
    this.state = {
      content: "",
    }

    this.handleContentChange = this.handleContentChange.bind(this)
  }

  handleContentChange(event) {
    this.setState({
      content: event.target.value,
    })
  }

  render() {
    const { content } = this.state
    const { userFirstName, userImage, tour, slug } = this.props
    return (
      <CommentWrap>
        <CommentField>
          <FieldName>Leave your comment</FieldName>
          <FieldInput>
            <InputArea
              value={this.state.content}
              onChange={this.handleContentChange}
              placeholder="Please leave your comment here!"
            />
          </FieldInput>
        </CommentField>
        <Button
          onClick={() =>
            this.props.sendComment(
              userFirstName,
              userImage,
              content,
              tour,
              slug
            )
          }
        >
          Post
        </Button>
      </CommentWrap>
    )
  }
}

const mapStateToProps = (state) => ({
  isLoggedIn: state.header.isLoggedIn,
  userFirstName: state.header.userFirstName,
  userImage: state.header.userImage,
  tour: state.tour.tour,
})

const mapDispatchToProps = (dispatch) => ({
  sendComment(userFirstName, userImage, content, tour, slug) {
    dispatch(
      actionCreators.sendComment(userFirstName, userImage, content, tour, slug)
    )
  },
})

export default connect(mapStateToProps, mapDispatchToProps)(CommentInput)
